hudson.tasks.Shell.command:
pipeline {
    agent any
    environment {
        BACKUP_DIR = "/tmp/jenkins_backup"
        DATE = sh(script: "date +%F_%H-%M", returnStdout: true).trim()
        TAR_FILE = "jenkins_backup_${DATE}.tar.gz"
        GIT_REPO = "git@github.com:mdnisarga9/pipline.git"
    }
    stages {
        stage('Backup Jenkins') {
            steps {
                script {
                    sh '''
                        # Clean backup dir
                        rm -rf $BACKUP_DIR
                        mkdir -p $BACKUP_DIR

                        # Create tar.gz of JENKINS_HOME
                        tar -czf $BACKUP_DIR/$TAR_FILE -C $JENKINS_HOME .
                    '''
                }
            }
        }
        stage('Push to GitHub') {
            steps {
                script {
                    sh '''
                        cd /tmp
                        rm -rf repo_clone
                        git clone $GIT_REPO repo_clone
                        cp $BACKUP_DIR/$TAR_FILE repo_clone/

                        cd repo_clone
                        git config user.email "you@example.com"
                        git config user.name "Jenkins Backup Bot"

                        git add .
                        git commit -m "Jenkins backup on $(date)"
                        git push origin main || git push origin master
                    '''
                }
            }
        }
    }
}
